function fatherFn(){
  this.some = "父类的this属性"
}
fatherFn.prototype.fatherFnSome = '父类原型对象的属性或方法'
//子类
function sonFn(){
  this.obkoro1 = "子类的this属性"
};
//核心步骤，重写子类的属性或者方法
sonfn.prototype = new fatherFn();//将fatherFn的实例赋值给sonFn的prototype
sonFn.prototype.sonFnSome = "子类原型队形的属性或方法"//子类的属性/方法声明在后面，避免被覆盖
